sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDateToday:function(strDate){
		//Create GlideDateTime objects for the current date and the passed date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		//Get data portion of rightnow and testdate ( no timestamp )
		var today = rightnow.getLocalDate();
		var isToday = testdate.getLocalDate();
		//If today is equal to isToday return true, else return false.
		if ( today.compareTo(isToday) == 0 ){
			return true;
		} else {
			return false;
		}

	},

	isDatePast:function(strDate){
			//Create GlideDateTime objects for the current date and the passed date
			var rightnow = new GlideDateTime();
			var testdate = new GlideDateTime(strDate);
			//if the testdate is before righnow, return true, else return false
			if (testdate.before(rightnow)){
				return true;
			} else {
				return false;
			}
	},

    type: 'NeedItUtils'
};
